import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Send, Calendar, Users, Clock, Mail, Plus, X } from 'lucide-react';

function App() {
  const [users, setUsers] = useState([]);
  const [chats, setChats] = useState([]);
  const [meetings, setMeetings] = useState([]);
  const [currentUser, setCurrentUser] = useState({ name: '', email: '' });
  const [newMessage, setNewMessage] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('chat');
  const [showNewUserForm, setShowNewUserForm] = useState(false);
  const [newUserData, setNewUserData] = useState({ name: '', email: '' });

  // Sample users for demo
  const sampleUsers = [
    { name: 'Alice Johnson', email: 'alice@example.com' },
    { name: 'Bob Smith', email: 'bob@example.com' },
    { name: 'Carol Davis', email: 'carol@example.com' },
    { name: 'David Wilson', email: 'david@example.com' }
  ];

  // Sample chat messages for demo
  const sampleChats = [
    { user_name: 'Alice Johnson', user_email: 'alice@example.com', message: 'Hey team! We need to discuss the Q4 project timeline.', timestamp: '2024-01-15T10:00:00' },
    { user_name: 'Bob Smith', user_email: 'bob@example.com', message: 'Good idea! I\'m available tomorrow afternoon.', timestamp: '2024-01-15T10:05:00' },
    { user_name: 'Carol Davis', user_email: 'carol@example.com', message: 'I can do tomorrow at 2 PM or Thursday morning.', timestamp: '2024-01-15T10:10:00' },
    { user_name: 'David Wilson', user_email: 'david@example.com', message: 'Tomorrow 2 PM works for me too!', timestamp: '2024-01-15T10:15:00' },
    { user_name: 'Alice Johnson', user_email: 'alice@example.com', message: 'Perfect! Let\'s schedule it for tomorrow at 2 PM.', timestamp: '2024-01-15T10:20:00' }
  ];

  useEffect(() => {
    // Load sample data
    setUsers(sampleUsers);
    setChats(sampleChats);
    setCurrentUser(sampleUsers[0]);
  }, []);

  const addNewUser = async () => {
    if (!newUserData.name.trim() || !newUserData.email.trim()) {
      alert('Please fill in both name and email');
      return;
    }

    try {
      const response = await axios.post('/api/users', newUserData);
      setUsers([...users, response.data]);
      setCurrentUser(response.data);
      setNewUserData({ name: '', email: '' });
      setShowNewUserForm(false);
    } catch (error) {
      console.error('Error adding user:', error);
      // For demo, add to local state
      const newUser = { ...newUserData, created_at: new Date().toISOString() };
      setUsers([...users, newUser]);
      setCurrentUser(newUser);
      setNewUserData({ name: '', email: '' });
      setShowNewUserForm(false);
    }
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || !currentUser.name) return;

    const message = {
      user_name: currentUser.name,
      user_email: currentUser.email,
      message: newMessage
    };

    try {
      const response = await axios.post('/api/chats', message);
      setChats([...chats, response.data]);
      setNewMessage('');
    } catch (error) {
      console.error('Error sending message:', error);
      // For demo, add to local state
      setChats([...chats, { ...message, timestamp: new Date().toISOString() }]);
      setNewMessage('');
    }
  };

  const analyzeChat = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/api/analyze-chat');
      setAnalysis(response.data);
    } catch (error) {
      console.error('Error analyzing chat:', error);
      // Demo analysis
      setAnalysis({
        has_meeting_intent: true,
        participants: [
          { name: 'Alice Johnson', email: 'alice@example.com' },
          { name: 'Bob Smith', email: 'bob@example.com' },
          { name: 'Carol Davis', email: 'carol@example.com' },
          { name: 'David Wilson', email: 'david@example.com' }
        ],
        availability_mentions: [
          {
            person: 'Bob Smith',
            availability: 'tomorrow afternoon',
            time_mentions: ['tomorrow afternoon']
          },
          {
            person: 'Carol Davis',
            availability: 'tomorrow at 2 PM or Thursday morning',
            time_mentions: ['tomorrow at 2 PM', 'Thursday morning']
          },
          {
            person: 'David Wilson',
            availability: 'tomorrow 2 PM',
            time_mentions: ['tomorrow 2 PM']
          }
        ],
        suggested_times: ['2024-01-16 14:00'],
        missing_info: [],
        follow_up_questions: []
      });
    } finally {
      setLoading(false);
    }
  };

  const scheduleMeeting = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/api/schedule-meeting', {
        title: 'Q4 Project Discussion'
      });
      setMeetings([...meetings, response.data]);
      alert('Meeting scheduled successfully! Check your email for confirmation.');
    } catch (error) {
      console.error('Error scheduling meeting:', error);
      // Demo meeting
      const demoMeeting = {
        title: 'Q4 Project Discussion',
        proposed_time: '2024-01-16 14:00',
        timezone: 'UTC',
        duration: '60 minutes',
        participants: ['Alice Johnson', 'Bob Smith', 'Carol Davis', 'David Wilson'],
        reasoning: 'Based on availability mentioned in chat',
        status: 'proposed',
        created_at: new Date().toISOString()
      };
      setMeetings([...meetings, demoMeeting]);
      alert('Meeting scheduled successfully! Check your email for confirmation.');
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div className="container">
      <div className="header">
        <h1>🤖 Meeting Scheduler AI</h1>
        <p>AI-powered meeting scheduling based on group chat analysis</p>
      </div>

      <div className="card">
        <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
          <button 
            className={`btn ${activeTab === 'chat' ? 'active' : ''}`}
            onClick={() => setActiveTab('chat')}
            style={{ flex: 1 }}
          >
            💬 Group Chat
          </button>
          <button 
            className={`btn ${activeTab === 'analysis' ? 'active' : ''}`}
            onClick={() => setActiveTab('analysis')}
            style={{ flex: 1 }}
          >
            🔍 AI Analysis
          </button>
          <button 
            className={`btn ${activeTab === 'meetings' ? 'active' : ''}`}
            onClick={() => setActiveTab('meetings')}
            style={{ flex: 1 }}
          >
            📅 Meetings
          </button>
        </div>

        {activeTab === 'chat' && (
          <div>
            <div className="card">
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
                <h3>👤 Current User</h3>
                <button 
                  className="btn" 
                  onClick={() => setShowNewUserForm(true)}
                  style={{ padding: '8px 16px', fontSize: '14px' }}
                >
                  <Plus size={16} /> New User
                </button>
              </div>

              {showNewUserForm ? (
                <div style={{ 
                  padding: '15px', 
                  border: '2px solid #667eea', 
                  borderRadius: '8px', 
                  marginBottom: '15px',
                  background: '#f8f9fa'
                }}>
                  <h4>➕ Add New User</h4>
                  <div style={{ marginBottom: '10px' }}>
                    <input
                      type="text"
                      className="input"
                      placeholder="Enter name..."
                      value={newUserData.name}
                      onChange={(e) => setNewUserData({...newUserData, name: e.target.value})}
                    />
                  </div>
                  <div style={{ marginBottom: '15px' }}>
                    <input
                      type="email"
                      className="input"
                      placeholder="Enter email..."
                      value={newUserData.email}
                      onChange={(e) => setNewUserData({...newUserData, email: e.target.value})}
                    />
                  </div>
                  <div style={{ display: 'flex', gap: '10px' }}>
                    <button className="btn" onClick={addNewUser}>
                      Add User
                    </button>
                    <button 
                      className="btn" 
                      onClick={() => setShowNewUserForm(false)}
                      style={{ background: '#6c757d' }}
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              ) : (
                <select 
                  className="input"
                  value={currentUser.name}
                  onChange={(e) => {
                    const user = users.find(u => u.name === e.target.value);
                    setCurrentUser(user);
                  }}
                >
                  {users.map(user => (
                    <option key={user.email} value={user.name}>
                      {user.name} ({user.email})
                    </option>
                  ))}
                </select>
              )}
            </div>

            <div className="card">
              <h3>💬 Group Chat</h3>
              <div className="chat-container">
                {chats.map((chat, index) => (
                  <div 
                    key={index} 
                    className={`message ${chat.user_name === currentUser.name ? 'sent' : 'received'}`}
                  >
                    <div className="message-header">
                      {chat.user_name} • {new Date(chat.timestamp).toLocaleString()}
                    </div>
                    {chat.message}
                  </div>
                ))}
              </div>

              <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <input
                  type="text"
                  className="input"
                  placeholder="Type your message..."
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                />
                <button className="btn" onClick={sendMessage}>
                  <Send size={20} />
                </button>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'analysis' && (
          <div className="grid">
            <div className="card">
              <h3>🔍 AI Analysis</h3>
              <button className="btn" onClick={analyzeChat} disabled={loading}>
                {loading ? 'Analyzing...' : 'Analyze Chat'}
              </button>

              {loading && (
                <div className="loading">
                  <div className="spinner"></div>
                </div>
              )}

              {analysis && (
                <div style={{ marginTop: '20px' }}>
                  <h4>Analysis Results:</h4>
                  <p><strong>Meeting Intent:</strong> {analysis.has_meeting_intent ? '✅ Yes' : '❌ No'}</p>

                  {analysis.participants.length > 0 && (
                    <div>
                      <h4>👥 Participants:</h4>
                      <ul>
                        {analysis.participants.map((participant, index) => (
                          <li key={index}>{participant.name} ({participant.email})</li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {analysis.availability_mentions.length > 0 && (
                    <div>
                      <h4>⏰ Availability Mentions:</h4>
                      {analysis.availability_mentions.map((mention, index) => (
                        <div key={index} style={{ marginBottom: '10px', padding: '10px', background: '#f8f9fa', borderRadius: '8px' }}>
                          <strong>{mention.person}:</strong> {mention.availability}
                        </div>
                      ))}
                    </div>
                  )}

                  {analysis.suggested_times.length > 0 && (
                    <div>
                      <h4>📅 Suggested Times:</h4>
                      <ul>
                        {analysis.suggested_times.map((time, index) => (
                          <li key={index}>{time}</li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {analysis.missing_info.length > 0 && (
                    <div>
                      <h4>❓ Missing Information:</h4>
                      <ul>
                        {analysis.missing_info.map((info, index) => (
                          <li key={index}>{info}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              )}
            </div>

            <div className="card">
              <h3>📅 Schedule Meeting</h3>
              <p>Based on the AI analysis, we can automatically schedule a meeting for the group.</p>

              <button 
                className="btn" 
                onClick={scheduleMeeting} 
                disabled={loading || !analysis?.has_meeting_intent}
                style={{ marginTop: '20px' }}
              >
                {loading ? 'Scheduling...' : 'Schedule Meeting'}
              </button>

              {analysis && analysis.has_meeting_intent && (
                <div style={{ marginTop: '20px', padding: '15px', background: '#e8f5e8', borderRadius: '8px' }}>
                  <h4>✅ Meeting Intent Detected!</h4>
                  <p>The AI has detected meeting scheduling intent in your conversation. Click the button above to schedule the meeting.</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'meetings' && (
          <div className="card">
            <h3>📅 Scheduled Meetings</h3>
            {meetings.length === 0 ? (
              <p>No meetings scheduled yet. Use the AI Analysis tab to schedule a meeting based on your chat.</p>
            ) : (
              <div>
                {meetings.map((meeting, index) => (
                  <div key={index} style={{ 
                    padding: '15px', 
                    border: '1px solid #e1e5e9', 
                    borderRadius: '8px', 
                    marginBottom: '15px',
                    background: '#f8f9fa'
                  }}>
                    <h4>{meeting.title}</h4>
                    <p><strong>Time:</strong> {meeting.proposed_time} ({meeting.timezone})</p>
                    <p><strong>Duration:</strong> {meeting.duration}</p>
                    <p><strong>Participants:</strong> {meeting.participants.join(', ')}</p>
                    <p><strong>Status:</strong> {meeting.status}</p>
                    <p><strong>Reasoning:</strong> {meeting.reasoning}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
